import FieldSelf from './FieldSelf'
import FieldOpponent from './FieldOpponent'
import Opponent from './Opponent'
import Self from './Self'
import Hand from './Hand'
import React from 'react'
import axios from 'axios'
import { isConstructorDeclaration } from 'typescript'




class Board extends React.Component {

    componentDidMount() {
        this.timer = setInterval(()=> this.getGame(), 1000);

      }

      componentWillUnmount() {
        this.timer = null;
      }

      getGame() {
        //axios.get
        //console.log("Interval 1000");
        axios.get('http://localhost:8080/game/users/garg.pranav@gmail.com').then(function (response) { console.log(response)} );
      }

    render() {
      return (<header className="App-header">
      <div className='container'>
        <Opponent/>
        <FieldOpponent/>
        <FieldSelf/>
        <Self/>
      </div>
      <div className='container'>
        <Hand/>
      </div>
    </header>);
    }
  }
  export default Board
