import FieldSelf from './FieldSelf'
import FieldOpponent from './FieldOpponent'
import Opponent from './Opponent'
import Self from './Self'
import Hand from './Hand'
import React, {Component} from 'react'
import axios from 'axios'
import { isConstructorDeclaration } from 'typescript'




class Board extends React.Component { 
  //function Board({parentToChild}){

    componentDidMount() {
        this.timer = setInterval(()=> this.getGame(), 1000);
        console.log(this.props.parentToChild);

      }

    //   componentWillUnmount() {
    //     this.timer = null;
    //   }

      getGame() {
        //axios.get
        //console.log("Interval 1000");
        //axios.get('http://localhost:8080/game/users/garg.pranav@gmail.com').then(function (response) { console.log(response)} );
        if(this.props.parentToChild != null){
          let url = `http://localhost:8080/game/users/${this.props.parentToChild}`;
          axios.get(`${url}`).then(function (response) { console.log(response)} );
        }
        console.log(this.props.parentToChild);
      }

    render(props) {
      return (<header className="App-header">
      <div className='container'>
        <Opponent/>
        <FieldOpponent/>
        <FieldSelf/>
        <Self/>
      </div>
      <div className='container'>
        <Hand/>
      </div>
      <div>{this.props.parentToChild}</div>
    </header>);
    }
  }
  export default Board
